import mongoose, { Document, Schema } from "mongoose";
import Joi, { validate } from "joi";

export interface IProduct extends Document {
    category_id: any;
    admin_id: any;
    price_for_each: number;
    name: string;
    amount: number;
    image_url: string;
}
export interface IProductInput {
    category_id: any;
    admin_id: any;
    name: string;
    price_for_each: number;
    amount: number;
    image_url: string;
}

const ProductSchema: Schema = new Schema({
    category_id: {
        type: Schema.Types.ObjectId,
        ref: 'Category',
        required: true
    },
    admin_id: {
        type: Schema.Types.ObjectId,
        ref: 'User',
        required: true
    },
    name: {
        type: Schema.Types.String,
        required: true
    },
    price_for_each: {
        type: Schema.Types.Number,
        required: true,
        min: 0.5
    },
    amount: {
        type: Schema.Types.Number,
        required: true,
        min: 1
    },
    image_url: {
        type: String,
        required: true
    }
})
export default mongoose.model<IProduct>("Product", ProductSchema);

export function validateProduct(product: IProductInput) {
    const schema = {
        admin_id: Joi.required(),
        category_id: Joi.required(),
        name: Joi.string().required().min(5).max(50),
        price_for_each: Joi.number().min(0.5).max(1000000),
        amount: Joi.number().min(1),
        image_url: Joi.string().required().min(20)
    }
    return validate(product, schema);
}

import mongoose, { Document, Schema } from "mongoose";
import joi, { validate } from "joi";

export interface IUser extends Document {
    fullName: string;
    address: string;
    email: string;
    password: string;
    isAdmin: boolean;
}
export interface IUserInput{
    fullName: string;
    address: string;
    email: string;
    password: string;
    isAdmin?: boolean;
}
export interface ILogin {
    email: string;
    password: string;
}
const UserSchema: Schema = new Schema({
    fullName: {
        type: Schema.Types.String,
        required: true
    },
    address: {
        type: Schema.Types.String,
        required: true,
        minlength: 5,
        maxlength: 50
    },
    email: {
        type: Schema.Types.String,
        required: true,
        unique: true
    },
    password: {
        type: Schema.Types.String,
        required: true
    },
    isAdmin: {
        type: Schema.Types.Boolean,
        required: true,
        default: false
    }
})
export default mongoose.model<IUser>("User", UserSchema);

export function validateUser(user: IUserInput) {
    const schema = {
        fullName: joi.string().min(5).max(50).required(),
        address: joi.string().min(5).max(50).required(),
        password: joi.string().min(5).max(25).required(),
        email: joi.string().email().required(),
        isAdmin: joi.boolean().default(false)
    }
    return validate(user, schema);
}

import mongoose, { Document, Schema } from "mongoose";

import Joi, { validate } from "joi";

export interface ICartDetails {
    productId: string;
    amountBuying: number;
}
export interface ICart extends Document {
    userId: string;
    products: ICartDetails[];
    date: Date;
    sum: number;
}
export interface ICartValidator {
    userId: string;
    product: ICartDetails;
    date?: Date;
}
const CartSchema: Schema = new Schema({
    userId: {
        type: Schema.Types.ObjectId,
        ref: "User",
        required: true
    },
    products: {
        type: Schema.Types.Array,
        required: true,
        default: []
    },
    date: {
        type: Schema.Types.Date,
        required: true,
        default: Date.now()
    },
    sum: {
        type: Schema.Types.Number,
        required: true,
        default: 0
    }
})
export default mongoose.model<ICart>("Cart", CartSchema);

export function validateCart(model: ICartValidator) {
    const schema = {
        userId: Joi.string().required(),
        product: Joi.object().required(),
        date: Joi.date().default(Date.now()),
    }
    return validate(model, schema);
}

